>>> 1: /**
 * API Key Connection Testing Service
 * Provides real-time testing of API keys against actual provider endpoints
 * Part of the enhanced API key validation system
 */

const https = require('https');
const logger = require('./logger');

class ApiKeyTesting {
  constructor() {
    this.supportedProviders = ['alpaca', 'tdameritrade', 'td_ameritrade', 'polygon', 'finnhub'];
    this.connectionTimeout = 5000; // 5 seconds
  }

  /**
   * Test API key connection with the actual provider
   * @param {string} provider - API provider name
   * @param {string} apiKey - API key to test
   * @param {string} apiSecret - API secret (if required)
   * @returns {Object} Test result with connection status and details
   */
  async testConnection(provider, apiKey, apiSecret = null) {
    const testStartTime = Date.now();

    try {
      logger.info('ðŸ§ª Starting API key connection test', {
        provider: provider,
        keyLength: apiKey ? apiKey.length : 0,
        hasSecret: !!apiSecret
      });

      const providerLower = provider.toLowerCase().trim();

      if (!this.supportedProviders.includes(providerLower)) {
        return {
          success: false,
          error: `Connection testing not implemented for provider: ${provider}`,
          errorCode: 'PROVIDER_NOT_SUPPORTED',
          testTime: Date.now() - testStartTime,
          suggestions: ['Use a supported provider like Alpaca or TD Ameritrade']
        };
      }

      switch (providerLower) {
        case 'alpaca':
          return await this._testAlpacaConnection(apiKey, apiSecret, testStartTime);

        case 'tdameritrade':
        case 'td_ameritrade':
          return await this._testTdAmeritradeConnection(apiKey, testStartTime);

        case 'polygon':
          return await this._testPolygonConnection(apiKey, testStartTime);

        case 'finnhub':
          return await this._testFinnhubConnection(apiKey, testStartTime);

        default:
          return {
            success: false,
            error: `Provider ${provider} testing not implemented`,
            errorCode: 'PROVIDER_NOT_IMPLEMENTED',
            testTime: Date.now() - testStartTime,
            suggestions: ['Use Alpaca or TD Ameritrade for connection testing']
          };
      }

    } catch (error) {
      logger.error('âŒ API key connection test failed', {
        provider: provider,
        error: error.message,
        testTime: Date.now() - testStartTime
      });

      return {
        success: false,
        error: `Connection test failed: ${error.message}`,
        errorCode: 'CONNECTION_TEST_ERROR',
        testTime: Date.now() - testStartTime,
        suggestions: ['Check your network connection and try again']
      };
    }
  }

  /**
   * Test Alpaca API connection
   */
  async _testAlpacaConnection(apiKey, apiSecret, startTime) {
    return new Promise((resolve) => {
      const options = {
        hostname: 'paper-api.alpaca.markets',
        path: '/v2/account',
        method: 'GET',
        headers: {
          'APCA-API-KEY-ID': apiKey,
          'APCA-API-SECRET-KEY': apiSecret,
          'User-Agent': 'Financial-Platform/1.0'
        },
        timeout: this.connectionTimeout
      };

      const req = https.request(options, (res) => {
        let data = '';

        res.on('data', chunk => {
          data += chunk;
        });

        res.on('end', () => {
          const testTime = Date.now() - startTime;

          if (res.statusCode === 200) {
            try {
              const accountData = JSON.parse(data);
              logger.info('âœ… Alpaca connection test successful', {
                statusCode: res.statusCode,
                testTime: testTime,
                accountId: accountData.id ? accountData.id.substring(0, 8) + '...' : 'unknown'
              });

              resolve({
                success: true,
                statusCode: res.statusCode,
                testTime: testTime,
                message: 'Successfully connected to Alpaca API',
                accountInfo: {
                  accountId: accountData.id ? accountData.id.substring(0, 8) + '...' : 'unknown',
                  status: accountData.status || 'unknown',
                  tradingBlocked: accountData.trading_blocked || false
                },
                suggestions: []
              });
            } catch (parseError) {
              resolve({
                success: true,
                statusCode: res.statusCode,
                testTime: testTime,
                message: 'Connected to Alpaca API (response parse warning)',
                warning: 'Could not parse account data',
                suggestions: []
              });
            }
          } else if (res.statusCode === 401) {
            resolve({
              success: false,
              statusCode: res.statusCode,
              error: 'Invalid Alpaca API credentials',
              errorCode: 'ALPACA_INVALID_CREDENTIALS',
              testTime: testTime,
              suggestions: [
                'Check your Alpaca API Key ID and Secret Key',
                'Ensure you\'re using the correct paper trading credentials',
                'Verify your keys are active and not expired'
              ]
            });
          } else if (res.statusCode === 403) {
